import React, { Component } from 'react';
import Projects from'./Components/projects';
import AddProject from './Components/AddProject'
import uuid from 'uuid';
import './App.css';
class App extends Component {
  constructor()
  {
  	super();
  	this.state={
  		projects:[]
  	}
}
  	componentWillMount(){
  		this.setState({projects:[
  		{
  			id:uuid.v4(),
  			title:'a',
  			name:'aa'
  		},
  		{
  			id:uuid.v4(),
  			title:'b',
  			name:'bb'
  		},
  		{
  			id:uuid.v4(),
  			title:'c',
  			name:'cc'
  		}]});
  	}

  handleAdd(project)
  {
  	console.log(project);
  	let data = this.state.projects;
  	data.push(project);
  	this.setState({projects:data})
  }
  handleDelete(id)
  {
  	let pro = this.state.projects;
  	let index = this.state.projects.findIndex(x=> x.id === id);
  	pro.splice(index, 1);
  	this.setState({projects:pro})
  }
  render() {
    return (
      <div className="App">

       My App
       <Projects projects={this.state.projects} onDelete = {this.handleDelete.bind(this)} />
       <AddProject addProject = {this.handleAdd.bind(this)}/>
      </div>
    );
  }
}

export default App;
